Source code:
function logEvent(event:Event):void {
	console.text += event.toString() + '\n';
	
	scrollPane.redraw();
}

function clearLog(event:Event = null):void {
	console.text = '';
	scrollPane.redraw();
}

function onMouseDown(event:Event):void {
	logEvent(event);
	
	dragArea.addEventListener(MouseEvent.MOUSE_MOVE, logEvent);
	this.stage.addEventListener(MouseEvent.MOUSE_UP, onMouseUp);
	this.stage.addEventListener(Event.MOUSE_LEAVE, onMouseUp);
}

function onMouseUp(event:Event):void {
	logEvent(event);
	
	dragArea.removeEventListener(MouseEvent.MOUSE_MOVE, logEvent);
	this.stage.removeEventListener(MouseEvent.MOUSE_UP, onMouseUp);
	this.stage.removeEventListener(Event.MOUSE_LEAVE, onMouseUp);
}

dragArea.addEventListener(MouseEvent.MOUSE_DOWN, clearLog);
dragArea.addEventListener(MouseEvent.MOUSE_DOWN, onMouseDown);

